void drawcube(pair loc){
     filldraw(loc+b--loc--loc+a--loc+a+c--loc+a+b+c--loc+b+c--cycle,white);
     draw(loc+b--loc+a+b--loc+a+b+c^^loc+a+b--loc+a);
}

pair location[]={origin,b,a,2a,3a,3a+b,-c,3a-c,-2c,-2c+b,-2c+a,-2c+2a,-2c+3a,-2c+3a+b};

for(int i; i<14;++i){
   drawcube(location[i]);
}


