triple V = (3,2,2), W = (4,1,3), P = dot(V,W)/abs(W)^2*W, R = 2*P - V;

draw(V--R,dashed);
draw(0.85*P--(0.85*P + 0.15*(V - P))--(P + 0.15*(V - P)));
draw(O--V,Arrow3(6));
draw(P--W,Arrow3(6));
draw(O--P,Arrow3(6));
draw(O--R,Arrow3(6));
draw(O--3*I, Arrow3(6));
draw(O--3*J, Arrow3(6));
draw(O--3*K, Arrow3(6));

