t = -0.4*pi;
r = cos(2*t)/cos(t);
x = r*cos(t);
y = r*sin(t);
path foo = (x,y);

for (t = -0.4*pi; t <= 0.4*pi; t = t + 0.01) {
  r = cos(2*t)/cos(t);
  x = r*cos(t);
  y = r*sin(t);
